object Day6 {

    fun go() {
        part1()
    }

    private fun part1() {
        println("Part 1: Signal starts at: ${startOfSignal(input, 4)}")
        println("Part 2: Message starts at: ${startOfSignal(input, 14)}")
    }

    private fun startOfSignal(string: String, signalLength: Int): Int {
        for (i in 0..(string.length - signalLength)) {
            val substr = string.substring(i, i + signalLength)
            if (substr.toSet().size == substr.length) {
                return i + signalLength
            }
        }
        return -1
    }
}

